interface CategoriesFilterItem {
  title: string;
  path: string;
}

function convertToPath(categories: string[]): CategoriesFilterItem[] {
  return categories.map((category) => {
    return {
      title: String(category),
      path: category.toLowerCase().split(" ").join("-"),
    };
  });
}

